import Vue from 'vue'
import Router from 'vue-router'
import Login from './views/Login.vue'

Vue.use(Router)

var router = new Router({
  mode: 'history',
  base: process.env.BASE_URL,
  routes: [

    {
      path: '/',
      name: 'login',
      component: Login
    },




    {
      path: '/admin',
      meta: {
        requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
        power: "admin" //权限
      },
      component: () => import('./views/admin/admin.vue'),

      children: [{
          path: '/admin/index',
          name: 'admin_index',
          meta: {
            requireAuth: true,
            power: "admin",
          },
          component: () => import('./views/admin/admin_index.vue'),
        },

        {
          path: '/admin/comp',
          name: 'admin_comp',
          meta: {
            requireAuth: true,
            power: "admin"
          },
          component: () => import('./views/admin/admin_comp.vue'),
        },

        {
          path: '/admin/comp/add',
          name: 'admin_comp_add',
          meta: {
            requireAuth: true,
            power: "admin"
          },
          component: () => import('./views/admin/admin_comp_add.vue'),
        },

        {
          path: '/admin/project',
          name: 'admin_project',
          meta: {
            requireAuth: true,
            power: "admin"
          },
          component: () => import('./views/admin/admin_project.vue'),
        },
        {
          path: '/admin/gonggao',
          name: 'admin_gonggao',
          meta: {
            requireAuth: true,
            power: "admin"
          },
          component: () => import('./views/admin/admin_gonggao.vue'),
        },
        {
          path: '/admin/tongji',
          name: 'admin_tongji',
          meta: {
            requireAuth: true,
            power: "admin"
          },
          component: () => import('./views/admin/admin_tongji.vue'),
        },
        {
          path: '',
          redirect: '/admin/index'

        },
      ]
    },
    //学院
    {
      path: '/school',
      meta: {
        requireAuth: true,
        power: "school"
      },
      component: () => import('./views/school/school.vue'),
      children: [{
          path: '/school/index',
          name: 'school_index',
          meta: {
            requireAuth: true,
            power: "school"
          },
          component: () => import('./views/school/school_index.vue'),
        },
        {
          path: '/school/project',
          name: 'school_project',
          meta: {
            requireAuth: true,
            power: "school"
          },
          component: () => import('./views/school/school_project.vue'),
        },
        {
          path: '/school/applyForProject',
          name: 'school_applyForProject',
          meta: {
            requireAuth: true,
            power: "school"
          },
          component: () => import('./views/school/school_applyForProject.vue'),
        },
        {
          path: '/school/studentPutin',
          name: 'school_studentPutin',
          meta: {
            requireAuth: true,
            power: "school"
          },
          component: () => import('./views/school/school_studentPutin.vue'),
        },

        {
          path: '/school/xinxi',
          name: 'school_xinxi',
          meta: {
            requireAuth: true,
            power: "school"
          },
          component: () => import('./views/school/school_xinxi.vue'),
        },

        {
          path: '',
          redirect: '/school/index'

        },
      ]
    },
    //学生
    {
      path: '/student',
      meta: {
        requireAuth: true,
        power: "student"
      },
      component: () => import('./views/student/student.vue'),
      children: [{
          path: '/student/index',
          name: 'student_index',
          meta: {
            requireAuth: true,
            power: "student"
          },
          component: () => import('./views/student/student_index.vue'),
        },
        {
          path: '/student/joinProject',
          name: 'student_joinProject',
          meta: {
            requireAuth: true,
            power: "student"
          },
          component: () => import('./views/student/student_joinProject.vue'),
        },
        {
          path: '/student/myProject',
          name: 'student_myProject',
          meta: {
            requireAuth: true,
            power: "student"
          },
          component: () => import('./views/student/student_myProject.vue'),
        },
        {
          path: '/student/xinxi',
          name: 'student_xinxi',
          meta: {
            requireAuth: true,
            power: "student"
          },
          component: () => import('./views/student/student_xinxi.vue'),
        },
        {
          path: '/student/tongji',
          name: 'student_tongji',
          meta: {
            requireAuth: true,
            power: "student"
          },
          component: () => import('./views/student/student_tongji.vue'),
        },

        {
          path: '',
          redirect: '/student/index'

        },
      ]
    },


    {
      path: '*',
      redirect: '/'
    }

  ]
})



export default router;

//路由守卫
router.beforeEach((to, from, next) => {
  var islogin = window.sessionStorage.getItem("islogin");
  var token = window.sessionStorage.getItem("token");
  var path = "/" + window.sessionStorage.getItem("token");
  // console.log("是否登录：" + islogin);
  // console.log("权限：" + token);
  if (to.meta.requireAuth) {
    // console.log("前往的页面: " + to.path + " 需要登陆");
    if (islogin == 'true') {
      // console.log("已经登录···");
      if (to.meta.power == token) {
        // console.log("拥有相应的访问权限: " + to.meta.power);
        next();
      } else {
        // console.log("没有相应的访问权限: " + to.meta.power);
        // console.log("前往拥有的权限页面: " + path);
        next(path);
      }
    } else {
      // console.log("没有登录，前往登陆页面···");
      next("/");
    }
  } else if (to.path == "/") {
    // console.log("前往的页面: " + to.path + " 不需要登陆");
    // console.log("前往的页面: " + to.path + " 是登录页面");
    // console.log();
    if (islogin == 'true') {
      // console.log("islogin: " + islogin + "已经登录···");
      // console.log("前往拥有的权限页面: " + path);
      next(path);
    } else {
      // console.log("islogin: " + islogin + "没有登录，登陆页面放行···");
      next();
    }
  } else {
    // console.log("前往的页面: " + to.path + " 不需要登陆");
    // console.log("前往的页面: " + to.path + " 不是登录页面，放行");
    next();
  }

})
