import * as echarts from 'echarts';
export default {
  name: "admin_tongji",
  data: function data() {
    return {
      tongji: {},
      proNum: 0,
      charts: ''
    };
  },
  mounted: function mounted() {
    this.init("bingzhuangtu"); // 按月份统计学生参加项目数量

    var lineChart = echarts.init(document.getElementById("lineChart"));
    lineChart.setOption({
      title: {
        text: '每月申请项目总数'
      },
      // 提示框
      tooltip: {
        trigger: 'axis'
      },
      // 图例
      legend: {
        icon: 'circle',
        left: 'center',
        top: 0,
        data: ['计算机与信息工程学院', '土木工程学院', '艺术学院']
      },
      grid: {
        left: '3%',
        right: '3%',
        bottom: '3%',
        containLabel: true
      },
      // 工具栏
      toolbox: {
        feature: {
          saveAsImage: {
            type: 'png'
          },
          magicType: {
            type: ['line', 'bar', 'stack']
          }
        }
      },
      xAxis: {
        type: 'category',
        boundaryGap: false,
        data: ['一月', '二月', '三月', '四月', '五月', '六月', '七月', '八月', '九月', '十月', '十一月', '十二月']
      },
      yAxis: {
        type: 'value'
      },
      series: [{
        name: '计算机与信息工程学院',
        type: 'line',
        // smooth: true, // 平滑曲线显示
        data: [120, 132, 101, 134, 190, 230, 210, 201, 234, 290, 230, 210]
      }, {
        name: '土木工程学院',
        type: 'line',
        // smooth: true,
        data: [100, 82, 91, 54, 90, 76, 110, 81, 104, 90, 130, 110]
      }, {
        name: '艺术学院',
        type: 'line',
        stack: '总量',
        // smooth: true,
        data: [10, 22, 21, 14, 19, 13, 20, 11, 34, 29, 20, 10]
      }]
    });
    var myChart = echarts.init(document.getElementById("myChart"));
    myChart.setOption({
      legend: {},
      tooltip: {
        trigger: 'axis',
        showContent: false
      },
      dataset: {
        source: [['product', '2012', '2013', '2014', '2015', '2016', '2017'], ['Milk Tea', 56.5, 82.1, 88.7, 70.1, 53.4, 85.1], ['Matcha Latte', 51.1, 51.4, 55.1, 53.3, 73.8, 68.7], ['Cheese Cocoa', 40.1, 62.2, 69.5, 36.4, 45.2, 32.5], ['Walnut Brownie', 25.2, 37.1, 41.2, 18, 33.9, 49.1]]
      },
      xAxis: {
        type: 'category'
      },
      yAxis: {
        gridIndex: 0
      },
      grid: {
        top: '55%'
      },
      series: [{
        type: 'line',
        smooth: true,
        seriesLayoutBy: 'row',
        emphasis: {
          focus: 'series'
        }
      }, {
        type: 'line',
        smooth: true,
        seriesLayoutBy: 'row',
        emphasis: {
          focus: 'series'
        }
      }, {
        type: 'line',
        smooth: true,
        seriesLayoutBy: 'row',
        emphasis: {
          focus: 'series'
        }
      }, {
        type: 'line',
        smooth: true,
        seriesLayoutBy: 'row',
        emphasis: {
          focus: 'series'
        }
      }, {
        type: 'pie',
        id: 'pie',
        radius: '30%',
        center: ['50%', '25%'],
        emphasis: {
          focus: 'data'
        },
        label: {
          formatter: '{b}: {@2012} ({d}%)'
        },
        encode: {
          itemName: 'product',
          value: '2012',
          tooltip: '2012'
        }
      }]
    });
    myChart.on('updateAxisPointer', function (event) {
      var xAxisInfo = event.axesInfo[0];

      if (xAxisInfo) {
        var dimension = xAxisInfo.value + 1;
        myChart.setOption({
          series: {
            id: 'pie',
            label: {
              formatter: '{b}: {@[' + dimension + ']} ({d}%)'
            },
            encode: {
              value: dimension,
              tooltip: dimension
            }
          }
        });
      }
    }); // 申请竞赛数量

    var mmyChart = echarts.init(document.getElementById("mmyChart"));
    mmyChart.setOption({
      title: {
        text: '32469',
        subtext: '申请竞赛数量',
        left: 'center',
        top: '43%',
        subtextStyle: {
          fontSize: 18
        }
      },
      tooltip: {
        trigger: 'item'
      },
      legend: {
        icon: 'circle',
        top: '0',
        left: 'right'
      },
      series: [{
        name: '咨询数量',
        type: 'pie',
        radius: ['40%', '55%'],
        label: {
          show: true,
          padding: [0, -60],
          overflow: 'none',
          fontSize: '15',
          fontWeight: 'bold',
          formatter: '{d}%\n\n{c}'
        },
        labelLine: {
          show: true,
          length: 15,
          length2: 60
        },
        itemStyle: {
          normal: {
            color: function color(params) {
              var colorList = ['#4FC3F7', '#00C853', '#F57F17'];
              return colorList[params.dataIndex];
            }
          }
        },
        data: [{
          name: '计算机与信息工程学院',
          value: 800
        }, {
          name: '土木工程学院',
          value: 200
        }, {
          name: '艺术学院',
          value: 180
        }]
      }]
    });
  },
  methods: {
    init: function init(id) {
      this.charts = echarts.init(document.getElementById(id));
      var img = 'data:image/png;base64,';
      var trafficWay = [{
        name: '火车',
        value: 20
      }, {
        name: '飞机',
        value: 10
      }, {
        name: '客车',
        value: 30
      }, {
        name: '轮渡',
        value: 40
      }];
      var data = [];
      var color = ['#00ffff', '#00cfff', '#006ced', '#ffe000', '#ffa800', '#ff5b00', '#ff3000'];

      for (var i = 0; i < trafficWay.length; i++) {
        data.push({
          value: trafficWay[i].value,
          name: trafficWay[i].name,
          itemStyle: {
            normal: {
              borderWidth: 5,
              shadowBlur: 20,
              borderColor: color[i],
              shadowColor: color[i]
            }
          }
        }, {
          value: 2,
          name: '',
          itemStyle: {
            normal: {
              label: {
                show: false
              },
              labelLine: {
                show: false
              },
              color: 'rgba(0, 0, 0, 0)',
              borderColor: 'rgba(0, 0, 0, 0)',
              borderWidth: 0
            }
          }
        });
      }

      var seriesOption = [{
        name: '',
        type: 'pie',
        clockWise: false,
        radius: [105, 109],
        hoverAnimation: false,
        itemStyle: {
          normal: {
            label: {
              show: true,
              position: 'outside',
              color: '#ddd',
              formatter: function formatter(params) {
                var percent = 0;
                var total = 0;

                for (var i = 0; i < trafficWay.length; i++) {
                  total += trafficWay[i].value;
                }

                percent = (params.value / total * 100).toFixed(0);

                if (params.name !== '') {
                  return '交通方式：' + params.name + '\n' + '\n' + '占百分比：' + percent + '%';
                } else {
                  return '';
                }
              }
            },
            labelLine: {
              length: 10,
              length2: 20,
              show: true,
              color: '#00ffff'
            }
          }
        },
        data: data
      }];
      var option = {
        backgroundColor: '#0A2E5D',
        color: color,
        title: {
          text: '交通方式',
          top: '48%',
          textAlign: "center",
          left: "49%",
          textStyle: {
            color: '#fff',
            fontSize: 22,
            fontWeight: '400'
          }
        },
        graphic: {
          elements: [{
            type: "image",
            z: 3,
            style: {
              image: img,
              width: 178,
              height: 178
            },
            left: 'center',
            top: 'center',
            position: [100, 100]
          }]
        },
        tooltip: {
          show: false
        },
        legend: {
          icon: "circle",
          orient: 'horizontal',
          // x: 'left',
          data: ['火车', '飞机', '客车', '轮渡'],
          right: 10,
          bottom: 10,
          align: 'right',
          textStyle: {
            color: "#fff"
          },
          itemGap: 10
        },
        toolbox: {
          show: false
        },
        series: seriesOption
      }; // 使用刚指定的配置项和数据显示图表。

      this.charts.setOption(option);
    }
  }
};
