import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    islogin: false,
    userinfo: {
      username: "",
      token: ""
    },
    studentName: ""
  },
  mutations: {
    loginTure(state, data) {
      state.islogin = data;
    },
    setUserinfo(state, data) {
      state.userinfo = data;
    },
    setUserinfoToken(state, data) {
      state.userinfo.token = data;
    },
    setUserinfoUsername(state, data) {
      state.userinfo.username = data;
    },
    logout(state) {
      state.userinfo = {};
      state.islogin = false;
    },
    setStudentName(state, name) {
      state.studentName = name;
    }
  },
  actions: {

  }
})
