export default {
  name: "home",
  data() {
    return {
      isXueyuan:false,
      form: {
        username: "",
        password: "",
        token: ""
      },
      rules: {
        username: [
          { required: true, message: "请输入用户名", trigger: "blur" }
        ],
        password: [
          { required: true, message: "请输入密码", trigger: "change" }
        ],
        token: [
          { required: true, message: "请选择登陆身份", trigger: "change" }
        ]
      }
    };
  },

  methods: {
    监听登陆身份(){
      if (this.form.token == "school") {
          this.isXueyuan = true;
      }else{
        this.isXueyuan = false;
      }
    },
    登录() {
      var that = this;
      this.$axios
        .post("http://localhost:8088/login", {
          username: that.form.username,
          password: that.form.password,
          token: that.form.token
        })
        .then(function(response) {
          console.log(response);
          switch (response.data.state) {
            case "2001":
              //登陆失败
              that.$message({
                message: response.data.message,
                type: "error"
              });
              break;
            case "2003":
              //登陆成功
              //更改store 登陆状态
              that.$store.commit("loginTure", true);
              that.$store.commit("setUserinfo", {
                username: response.data.username,
                token: response.data.token
              });
              //将username存入本地
              window.sessionStorage.setItem("username", response.data.username);
              //将token存入本地
              window.sessionStorage.setItem("token", response.data.token);
              //将islogin存入本地
              window.sessionStorage.setItem("islogin", true);
              that.$message({
                message: response.data.message,
                type: "success"
              });

              //跳转页面
              switch (response.data.token) {
                case "admin":
                  that.$router.push({ path: "/admin" });
                  break;
                case "school":
                  that.$router.push({ path: "/school" });
                  break;
                case "student":
                  that.$router.push({ path: "/student" });
                  break;
              }
              break;
            default:
              //提示信息
              that.$message({
                message: response.data.message,
                type: "error"
              });
              break;
          }
        })
        .catch(function(error) {
          console.log(error);
        });
    },
    submitForm(formName) {
      var that = this;
      this.$refs[formName].validate(valid => {
        if (valid) {
          that.登录();
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  },

  

};
